mapper.select:
SELECT ios.id, ios.warehouse_id as warehouseId, w.name as warehouseName, ios.project_id as projectId, p.name as project, ios.stock_id as stockId, s.product, s.model, s.unit, ios.count, ios.price, ios.source, ios.handle, ios.remark, ios.type, ios.create_time as createTime FROM inout_stock ios INNER JOIN stock s on ios.stock_id = s.id and ios.warehouse_id = s.warehouse_id LEFT JOIN project p on ios.project_id = p.id LEFT JOIN warehouse w ON ios.warehouse_id = w.id
AND ios.warehouse_id = #{warehouseId}
AND ios.type = #{type}
AND ios.create_time >= #{startTime}
AND ios.create_time <= #{endTime}
AND ios.source LIKE CONCAT('%', #{source}, '%')
AND p.name LIKE CONCAT('%', #{project}, '%')
AND s.product LIKE CONCAT('%', #{product}, '%')
AND s.model LIKE CONCAT('%', #{model}, '%')
order by ios.create_time desc
