mapper.select:
SELECT pr.id, pr.project_id as projectId, pr.type, pr.record_date as recordDate, pr.digest, pr.money, pr.remark, pr.create_time as createTime, pr.create_user_id as createUserId FROM project_record pr INNER JOIN project p on pr.project_id = p.id
AND pr.project_id = #{projectId}
AND pr.type = #{type}
AND pr.digest LIKE CONCAT('%', #{digest}, '%')
order by pr.create_time desc

SELECT project_id as projectId, type, sum(money) as money FROM project_record where project_id in
#{item}
group by project_id, type
